struct Patt{
	uint8_t xPos[7];
	uint8_t yPos[7];
	int8_t xDir[7];		//consider -1 means left, 1 means right 0 means no movement
	int8_t yDir[7];		// -1 means down 1 means up 0 means no movement
	uint8_t Speed[7];// x y Down/Up Left/Right
};
typedef struct Patt stype;		//redefines the struct type Patt as stype so we can use Patt in next line
stype Patt;					//allocates space for the Patt struct

struct Yerraballer{
	uint8_t xPos;
	uint8_t yPos;
	uint8_t xBall;
	uint8_t yBall;
	uint8_t Speed;
	uint8_t Ball;
};
typedef struct Yerraballer player;
player Yerraballer;

