uint8_t CollisionTB(void){
	uint8_t collision = 0;
	for(uint8_t i = 0; i<PattNum; i++){
		if(Yerraballer.yPos == (Patt.yPos[i] - PattH)){		//if BOTTOM of Yerraballi on same Y level as TOP of Patt
			for(uint8_t n = 0; n<YerrW; n++){		//checks if any of the bottom of Yerr is in the same X as top of Patt
				if((Patt.xPos[i]) == (Yerraballer.xPos + n)){
					collision++;
				}
				if((Patt.xPos[i] + PattW) == (Yerraballer.xPos + n)){
					collision++;
				}
			}
		}
		if((Yerraballer.yPos - YerrH) == Patt.yPos[i]){		//if TOP of Yerraballi on same Y level as BOTTOM of Patt
			for(uint8_t n = 0; n<YerrW; n++){		//checks if any of top of Yerr is in the same X as bottom of Patt
				if((Patt.xPos[i]) == (Yerraballer.xPos + n)){
					collision++;
				}
				if((Patt.xPos[i] + PattW) == (Yerraballer.xPos + n)){
					collision++;
				}
			}
		}
	}
	return(collision);
}

uint8_t CollisionLR(void){
	uint8_t collision = 0;
	for(uint8_t i = 0; i<PattNum; i++){
		if(Yerraballer.xPos == (Patt.xPos[i] + PattW)){		//if LEFT of Yerraballi on same X level as RIGHT of Patt
			for(uint8_t n = 0; n<YerrH; n++){		//checks if any of the left of Yerraballi is in the same Y as right of Patt
				if((Patt.yPos[i]) == (Yerraballer.yPos - n)){
					collision++;
				}
				if((Patt.yPos[i] - PattH) == (Yerraballer.yPos - n)){
					collision++;
				}
			}
		}
		if((Yerraballer.xPos + YerrW) == Patt.xPos[i]){		//if RIGHT of Yerraballi on same X level as LEFT of Patt
			for(uint8_t n = 0; n<YerrH; n++){		//checks if any of top of Yerraballi is in the sae Y as bottom of Patt
				if((Patt.yPos[i]) == (Yerraballer.yPos - n)){
					collision++;
				}
				if((Patt.yPos[i] - PattH) == (Yerraballer.yPos - n)){
					collision++;
				}
			}
		}
	}
	return(collision);
}

