void SwitchLED_Init(void){
	SYSCTL_RCGC2_R |= 0x10;
	FallingEdges = 0;
	GPIO_PORTE_DIR_R &= ~0x08;		//PE3 = game switch
	GPIO_PORTE_DIR_R |= 0x30;			//PE4-5 = LED
	GPIO_PORTE_DEN_R |= 0x38;
	GPIO_PORTE_AFSEL_R &= ~0x38;
	
	GPIO_PORTE_DIR_R &= ~0x03;    // (c) make PE0-1 in (built-in button)
  GPIO_PORTE_AFSEL_R &= ~0x03;  //     disable alt funct on PE0-1
  GPIO_PORTE_DEN_R |= 0x03;     //     enable digital I/O on PE0-1
  GPIO_PORTE_PCTL_R &= ~0x000000FF; //  configure PE0-1 as GPIO
  GPIO_PORTE_AMSEL_R &= ~0x03;  //    disable analog functionality on PE0-1
  GPIO_PORTE_IS_R &= ~0x03;     // (d) PE0-1 is edge-sensitive
  GPIO_PORTE_IBE_R |= 0x03;    //     PE0-1 is both edges
  GPIO_PORTE_ICR_R = 0x03;      // (e) clear flag0-1
  GPIO_PORTE_IM_R |= 0x03;      // (f) arm interrupt on PE0-1
  NVIC_PRI1_R = (NVIC_PRI1_R&0xFFFFFF00)|0x000000A0; // (g) priority 5
  NVIC_EN0_R = 0x00000010;      // (h) enable interrupt 30 in NVIC
}

void GPIOPortE_Handler(void){
	DisableInterrupts();
	//GPIO_PORTE_ICR_R = 0x03;
	if((GPIO_PORTE_RIS_R & 0x01) != 0){
		MoveYerraballi = 1;
	}
	if(((GPIO_PORTE_RIS_R & 0x02) != 0)){
		MoveYerraballi = -1;	
	}
	if((GPIO_PORTE_DATA_R & 0x03) == 0){
		MoveYerraballi = 0;
	}
	GPIO_PORTE_ICR_R = 0x03;
	EnableInterrupts();
}

uint32_t Convert(uint32_t input){
  uint32_t converted;
	converted = input*1;
	converted = (converted + 292);
	converted = (converted/43);

	return(converted);
}
